import React from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { NavLink } from "react-router-dom";
import { CardBody } from "reactstrap";

const NavigationItem = (props) => (
  <NavLink to={props.url} className={props.className}>
    <CardBody>
      <img src={props.thumb} width={32} height={32} alt={`${props.thumb} Thumbnail`} />
      {"    "}
      {props.name}
    </CardBody>
  </NavLink>
);

const mapStateToProps = (state, ownProps) => {
  const active = ownProps.url === state.router.location.pathname;
  const className = active ? "active-item" : "normal-item";
  return {active, className};
};

NavigationItem.propTypes = {
  name: PropTypes.string,
  thumb: PropTypes.string,
  url: PropTypes.string
};

export default connect(
  mapStateToProps
)(NavigationItem);
